import React from 'react';
import { getBottomSpace } from 'react-native-iphone-x-helper';
import { HomeCard } from '../../components/HomeCard';
import { Title } from '../../components/HomeCard/styles';
import { TransactionCard } from '../../components/TransactionCard';
import { TransactionList } from '../../components/TransactionCard/styles';
import { 
  Container,
  Header,
  UserInfo,
  Photo,
  User,
  UserGreeting,
  UserName,
  UserWapper,
  Icon,
  SliderCards,
  Transactions,
  Titulo} 
from './styles';

export function Dashboard(){

  const data = [{
      type:'positive',
      title : "Desenvolvimento de Site",
      amount : "R$ 12.000,00",
        category:{ 
          name: 'Vendas',
          icon:'dollar-sign' 
        },
      date:"12/12/2020"
  },
  {
    type:'negative',
    title : "Desenvolvimento de Site",
    amount : "R$ 12.000,00",
      category:{ 
        name: 'Vendas',
        icon:'dollar-sign' 
      },
    date:"12/12/2020"
},
{
  type:'positive',
  title : "Desenvolvimento de Site",
  amount : "R$ 12.000,00",
    category:{ 
      name: 'Vendas',
      icon:'dollar-sign' 
    },
  date:"12/12/2020"
}
]

    return (
        <Container>
          <Header>
            <UserWapper>
            <UserInfo>
              <Photo source={{ uri:'https://media-exp1.licdn.com/dms/image/C4E03AQFrzK8qXEbIxw/profile-displayphoto-shrink_100_100/0/1626086132319?e=1631750400&v=beta&t=PppWQYI2Gu89sk4dqLgD2xcHGX1HqkhvRYzjzPvpd0E' }} />
              <User>
                <UserGreeting>Olá,</UserGreeting>
                <UserName>Joabe</UserName>
              </User>
            </UserInfo>
            <Icon name="power"/>
            </UserWapper>
            
          </Header>
          <SliderCards>
            <HomeCard title="Entradas" amount="R$ 17.400,00" lastTransaction="Última entrada dia 13 de abril" type="up"></HomeCard>
            <HomeCard title="Saidas" amount="R$ 17.400,00" lastTransaction="Última entrada dia 13 de abril" type="down"></HomeCard>
            <HomeCard title="Entradas" amount="R$ 17.400,00" lastTransaction="Última entrada dia 13 de abril" type="total"></HomeCard>
          </SliderCards>
          <Transactions>
            <Titulo>Listagem</Titulo>
            <TransactionList
                data={data}
                renderItem={({item})=> <TransactionCard  data={item}  />}
                showsVerticalScrollIndicator={false}
                contentContainerStyle={{ paddingBottom: getBottomSpace() }}
            />
            
          </Transactions>
        </Container>
      );
}
